//@AutoGenerated
var IsConnected = false;
function btnConnect_Click()
{
	var btn = document.getElementById("btnConnect");
	if (!IsConnected)
	{
		IsConnected = true;
		btn.innerText = "Disconnect";
		var service = new WebService_Connected.ConnectedWebService();
		service.WaitForNewItems(1500, Connect_SucceededCallback, Connect_ErrorCallback);
	}
	else
	{
		IsConnected = false;
		btn.innerText = "Connect";
	}
}
function Connect_ErrorCallback(error, userContext, methodName)
{
	if (IsConnected)
	{
		var service = new WebService_Connected.ConnectedWebService();
		service.WaitForNewItems(1500, Connect_SucceededCallback, null);
	}
}
function Connect_SucceededCallback(result, context, methodName)
{
	if (IsConnected)
	{
		var customers = result;
		if (customers != null)
		{
			var divRepeater = document.getElementById("divRepeater1");
			for (var i = 0;i < customers.length;i++)
			{
				var cust = customers[i];
				var div = document.createElement("div");
				div.innerText = "ID=" + cust.ID + " Name=" + cust.Text;
				divRepeater.appendChild(div);
			}
		}
		var service = new WebService_Connected.ConnectedWebService();
		service.WaitForNewItems(1500, Connect_SucceededCallback, null);
	}
}
